public class Test3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//num1 에 정수 10, num2에 정수20, num3 = num1+num2, 연산과정을 출력
		int num1;
		num1 = 10;
		//변수 선언 후 처음 데이터를 저장하는것 -> 초기화
		int num2= 20; // 변수선언과 초기화가 한번에 정의
		int num3;
		num3 = num1 + num2;
		//int num3 = num1 + num2; 로 표현가능
		System.out.println(num1+"+"+num2+"="+num3);
		

	}

}